export function recursiveReplaceValueByName(name,object,value)
{
    var keysArr = name.replace(/\[/g,".").replace(/\]/g,"").split(".");

    var target = object;

    for (var i = 0; i < keysArr.length; i++){
        let key = keysArr[i];
        if (!target.hasOwnProperty(key)){
            return object;
        }

        if (i == keysArr.length - 1) {
            target[key] = value;
        }

        target = target[key];
    }

    return object;
}

export function handleChange(e,callBack,nullValue=null) {
    const {value, name} = e.target;
    if (typeof callBack !=="function"){
        callBack = function() {}
    }
    this.setStateByName(name, value!==""?value:null, callBack);
}

export function setStateByName(name, value, callBack) {
    this.setState(function(prevState) {
        return recursiveReplaceValueByName(name, prevState, value);
    },callBack)
}

