import { TextField as MaterialTextField } from '@material-ui/core';

export function TextField(props){

    const {
        readOnly = false,
        helperText,
        hint,
        value,
        tabIndex=undefined
    } = props;

    return (
        <MaterialTextField
            error = { helperText && helperText.length > 0 && helperText !== hint ?true:false }
            fullWidth
            size="small"
            variant="outlined"
            {...props}
            value={value?value:""}
            helperText={
                <span>
                    {
                        helperText && helperText.length>0?
                            <CancelIcon/>
                            :
                            ""
                    }
                    {helperText && helperText.length > 0 && helperText !== hint ?helperText:
                        (hint?
                                (<div><ErrorIcon/>{hint}</div>)
                                : ""
                        )
                    }
                </span>}
        />
    );
}
