import React from 'react';
import Grid from '@material-ui/core/Grid';
import styles from '../../css/Public.module.css';
import IconButton from '@material-ui/core/IconButton';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    icon: {
        fontSize:'1.5rem',
        color:'#8F8F8F',
        "&:hover":{
            color:'#1F1F1F'
        }
    }
});

export const Carousel = (props) => {

    const classes = useStyles();

    const [carousel, setCarousel] = React.useState(1);

    const handleCarouselNext = () => {
        setCarousel(2);
    }

    const handleCarouselLast = () => {
        setCarousel(1);
    }

    return(
        <React.Fragment>

            {carousel == 1?
                <div className={styles.carouselOuter}>
                    <Grid container style={{marginTop:'3%'}}>
                        <Grid item xs={false} md={2}></Grid>
                        <Grid item xs={12} md={8}>
                            <div style={{backgroundColor:'white'}}>
                                <img
                                    src={props.img1}
                                    className={styles.carouselImg}
                                />
                            </div>
                        </Grid>
                        <Grid item xs={false} md={2}></Grid>
                    </Grid>
                </div>
            :''}

            {carousel == 2?
                <div className={styles.carouselOuter}>
                    <Grid container style={{marginTop:'3%'}}>
                        <Grid item xs={false} md={2}></Grid>
                        <Grid item xs={12} md={8}>
                            <div style={{backgroundColor:'white'}}>
                                <img
                                    src={props.img2}
                                    className={styles.carouselImg}
                                />
                            </div>
                        </Grid>

                        <Grid item xs={false} md={2}></Grid>
                    </Grid>
                </div>
            :''}

            <Grid container className={styles.captionContainer}>
                <Grid item xs={false} md={2}></Grid>
                <Grid item xs={12} md={8}>
                    <Grid container>
                        <Grid item xs={3}>
                            <IconButton
                                onClick={handleCarouselLast}
                                disabled={carousel==1?true:false}
                                style={{backgroundColor: 'transparent'}}
                                disableRipple={true}
                            >
                                <ArrowBackIcon className={classes.icon}/>
                            </IconButton>
                        </Grid>

                        {carousel == 1?
                            <Grid item xs={6} style={{marginTop:'1.5%'}}>
                                <span className={styles.imgDescription}>
                                    {props.caption1}
                                </span>
                            </Grid>
                        :''}

                        {carousel == 2?        
                            <Grid item xs={6} style={{marginTop:'1.5%'}}>
                                <span className={styles.imgDescription}>
                                    {props.caption2}
                                </span>
                            </Grid>
                        :''}

                        <Grid item xs={3} style={{textAlign:'right'}}>
                            <IconButton
                                onClick={handleCarouselNext}
                                disabled={carousel==2?true:false}
                                style={{backgroundColor: 'transparent'}}
                                disableRipple={true}
                            >
                                <ArrowForwardIcon className={classes.icon}/>
                            </IconButton>
                        </Grid>
                    </Grid>
                </Grid>
                <Grid item xs={false} md={2}></Grid>
            </Grid>

        </React.Fragment>
    )
}
