import React from 'react';
import styles from '../../css/About.module.css';
import Grid from '@material-ui/core/Grid';
import { Header } from '../../component/Public/Header.js'; 
import { Footer } from '../../component/Home/Footer.js';
import { Aboutme } from '../../component/About/Aboutme.js';
import { Gallery } from '../../component/About/Gallery.js';
import { Experience } from '../../component/About/Experience.js';
import { Drawer } from '../../component/Public/Drawer.js';

const About = () => {

    const [drawer, setDrawer] = React.useState(false);

    const handleDrawerOpen = () => {
        setDrawer(true);
    }

    const handleDrawerClose = () => {
        setDrawer(false);
    }
    
    return(
        <React.Fragment>
            
            {drawer == false ?
                <React.Fragment>
                    <div>
                        <Header
                            handleDrawerOpen={handleDrawerOpen}
                        />
                    </div>

                    <div className={styles.main}>

                        <Grid container spacing={1} style={{marginTop:"3%"}}>
                            
                            <Grid item xs={1} sm={2} md={3}></Grid>
                            
                            <Grid item xs={10} sm={8} md={6}>
                                <span className={styles.title}>Hello World.</span>
                                <Aboutme/>
                            </Grid>
                            
                            <Grid item xs={1} sm={2} md={3}></Grid>

                        </Grid>

                        {/* <Grid container spacing={1} style={{marginTop:"8%"}}>

                            <Grid item xs={1} sm={2} md={3}></Grid>
                            
                            <Grid item xs={10} sm={8} md={6}>
                                <Experience/>
                            </Grid>
                            
                            <Grid item xs={1} sm={2} md={3}></Grid>

                        </Grid> */}

                    </div>

                    <div className={styles.footer}>
                        <Footer/>
                    </div>
                </React.Fragment>
            :''}

            {drawer == true ?
                <React.Fragment>
                    <div style={{backgroundColor:'#1f1f1f'}}>
                        <Drawer
                            handleDrawerClose={handleDrawerClose} 
                        />
                    </div>
                </React.Fragment>
            :''}

        </React.Fragment>
    )
}

export default About;
