import React, { useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';

export const Scrollup = (props) => {

    const [scrollC, setScrollC] = React.useState(0);

    const [fadeC, setFadeC] = React.useState(false);

    useEffect(()=>{
        window.addEventListener("scroll", onScroll);
        function onScroll() {
            setScrollC(window.scrollY);
        }  
    })

    const { handleScrolltoTop } = props;

    const fadeout = {
        opacity:0,
        width:0,
        height:0,
        transition: 'width 0.5s 0.5s, height 0.5s 0.5s, opacity 0.5s'
    }

    const fadein = {
        opacity:1,
        width:'100px',
        height:'100px',
        transition: 'width 0.5s, height 0.5s, opacity 0.5s 0.5'
    }

    return(
        <React.Fragment>
            <Grid container style={{align:'right'}}>
                <Grid item xs={11}></Grid>

                <Grid item xs={1}>

                    {scrollC > 400 ?
                        <Button 
                            onClick={handleScrolltoTop}
                            style={fadeout}
                        >
                            <ExpandLessIcon style={{fontSize:'4rem'}}/>
                        </Button>:''
                    }

                </Grid>

            </Grid>
        </React.Fragment>
    )
}
