import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import { Banner } from '../component/Home/Banner.js';
import { Showcase } from '../component/Home/Showcase.js';
import { Writings } from '../component/Home/Writings.js';
import { Footer } from '../component/Home/Footer.js';
import { Header } from '../component/Home/Header.js';
import Funfacts from '../component/Home/Funfacts.js';
import styles from '../css/Home.module.css';
import Button from '@material-ui/core/Button';
import { Drawer } from '../component/Public/Drawer.js';

const Index = () => {

    const [drawer, setDrawer] = React.useState(false);

    const handleDrawerOpen2 = () => {
        setDrawer(true);
    }

    const handleDrawerClose2 = () => {
        setDrawer(false);
    }

    return(
        <React.Fragment>

            {drawer == false ?
            <React.Fragment>
            {/* title & menu */}

            <div>
                <Header
                    handleDrawerOpen2={handleDrawerOpen2}
                    handleDrawerClose2={handleDrawerClose2}
                />
            </div>            

            {/* title & menu */}

            <div className={styles.main1}>

            {/* banner */}

                <Grid container spacing={1} style={{marginTop:"12%"}}>
                    <div>
                        <Banner/>
                    </div>

                </Grid>    

            {/* banner */}

            {/* showcase */}

                <Grid container spacing={1} style={{marginTop:"12%"}}>
                        <Showcase/>
                </Grid>

            {/* showcase */}

            {/* view all projects */}

                 <Grid container spacing={1} style={{marginTop:"8%", marginBottom:"8%"}}>
                    <Grid item sm={3}></Grid>
                    <Grid item xs={12} sm={6} style={{textAlign:'center'}}>
                        <a href='/Work' className={styles.viewLink}>VIEW ALL PROJECTS</a>
                    </Grid>
                    <Grid item md={3}></Grid>
                </Grid>

            {/* view all projects */}

            </div>

            {/* writings */}

            <div className={styles.main2}>
                <Writings/>
            </div>

            {/* writings */}

            {/* fun facts */}

            <div>
                <Funfacts/>
            </div>

            {/* fun facts */}

            {/* footer */}

            <div className={styles.main3}>
                <Footer/>
            </div>

            {/* footer */}
            </React.Fragment>
            :''}
            {drawer == true ?
            <React.Fragment>
                <div style={{height:'100%'}}>
                <Drawer
                   handleDrawerClose2={handleDrawerClose2} 
                />
                </div>
            </React.Fragment>
            :''}
        </React.Fragment>
    )
}

export default Index;
