import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import { Banner } from '../component/Home/Banner.js';
import { Showcase } from '../component/Home/Showcase.js';
import { Writings } from '../component/Home/Writings.js';
import { Footer } from '../component/Public/Footer.js';
import { Header } from '../component/Public/Header.js';
import Funfacts from '../component/Home/Funfacts.js';
import styles from '../css/Home.module.css';
import { Drawer } from '../component/Public/Drawer.js';
import { PageLoader } from '../component/Public/PageLoader.js';
import Typewriter from '../component/Public/Typer.js';
import Button from '@material-ui/core/Button';

const Index = () => {

    const [drawer, setDrawer] = React.useState(false);
    const [leak, setleak] = React.useState(false);

    const handleLeak = () => {
        setleak(true);
    }

    const handleDrawerOpen = () => {
        setDrawer(true);
    }

    const handleDrawerClose = () => {
        setDrawer(false);
    }

    return(
        <React.Fragment>

            {drawer == false ?
                <React.Fragment>
                {/* title & menu */}

                <PageLoader/>

                <div>
                    <Header
                        handleDrawerOpen={handleDrawerOpen}
                    />
                </div>            

                {/* title & menu */}

                <div className={styles.main1}>

                {/* banner */}

                    <Grid container spacing={1} style={{marginTop:"12%"}}>
                        <div>
                            <Banner/>
                            {leak == false ?
                            <Typewriter
                    rotateSpeed={800}
                    typeSpeed={80}
                    fontSize={'24px'}
                    fontFamily={'Roboto'}
                    color={'#6f6f6f'}
                    heading={'Typewriter'}
                    dataText={[ 'twooooo','onewwww','three']}
                />  
                :''}
                <Button onClick={handleLeak}>try</Button>
                        </div>

                    </Grid>    

                {/* banner */}

                {/* showcase */}

                    <Grid container spacing={1} style={{marginTop:"12%"}}>
                            <Showcase/>
                    </Grid>

                {/* showcase */}

                {/* view all projects */}

                    <Grid container spacing={1} style={{marginTop:"8%", marginBottom:"8%"}}>
                        <Grid item sm={3}></Grid>
                        <Grid item xs={12} sm={6} style={{textAlign:'center'}}>
                            <a href='/Work' className={styles.viewLink}>VIEW ALL PROJECTS</a>
                        </Grid>
                        <Grid item md={3}></Grid>
                    </Grid>

                {/* view all projects */}

                </div>

                {/* writings */}

                <div className={styles.main2}>
                    <Writings/>
                </div>

                {/* writings */}

                {/* fun facts */}

                <div>
                    <Funfacts/>
                </div>

                {/* fun facts */}

                {/* footer */}

                <div className={styles.main3}>
                    <Footer/>
                </div>

                {/* footer */}
                </React.Fragment>
            :''}

            {drawer == true ?
                <React.Fragment>
                    <div style={{backgroundColor:'#1f1f1f'}}>
                    <Drawer
                        handleDrawerClose={handleDrawerClose} 
                    />
                    </div>
                </React.Fragment>
            :''}

        </React.Fragment>
    )
}

export default Index;
