import React, { useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import Button from '@material-ui/core/Button';

const Carousel = (props) => {

    const [count, setCount] = React.useState(0);

    const {
        imgsrc,
        rotateBy,
        width,
        height,
        borderRadius
    } = props;

    useEffect(() => {
        const timer = setInterval(() => {
            setCount(count => count + 1);
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    const handleBack = () => {
        setCount(count => count - rotateBy);
    }

    const handleNext = () => {
        setCount(count => count + rotateBy);
    }

    const list = [];

    for(let i = 0; i <= imgsrc.length - 1; i++) {
        list.push(
            <React.Fragment>

                        {parseInt( count / rotateBy) % imgsrc.length === i ?
                            <div style={{width:width, height:height, borderRadius:borderRadius}}>
                                <img src={imgsrc[i]} style={{margin: '0 auto', width:'100%', height:'100%'}}/>
                            </div>
                        :''}

            </React.Fragment>
        )
    }

    return(
        <React.Fragment>

            <Grid container>
                {/* <Grid xs={12} style={{textAlign:'center'}}> */}
                    <Button variant='text' onClick={handleBack}>
                        <ArrowBackIosIcon/>
                    </Button>
                {/* </Grid> */}
                {/* <Grid xs={6}> */}
                    {list}
                {/* </Grid> */}
                {/* <Grid xs={3} style={{textAlign:'center'}}> */}
                    <Button startIcon={<ArrowForwardIosIcon/>} onClick={handleNext}>

                    </Button>
                {/* </Grid> */}
            </Grid>

        </React.Fragment>
    )
}

export default Carousel;
