import React, { useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import ArrowBackIosIcon from '@material-ui/icons/ArrowBackIos';
import ArrowForwardIosIcon from '@material-ui/icons/ArrowForwardIos';
import IconButton from '@material-ui/core/IconButton';

const Carousel = (props) => {

    const [count, setCount] = React.useState(0);

    const {
        imgsrc,
        rotateBy,
        width,
        height,
        borderRadius
    } = props;

    useEffect(() => {
        const timer = setInterval(() => {
            setCount(count => count + 1);
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    const handleBack = () => {
        setCount(count => count - rotateBy);
    }

    const handleNext = () => {
        setCount(count => count + rotateBy);
    }

    const list = [];

    for(let i = 0; i <= imgsrc.length - 1; i++) {
        list.push(
            <React.Fragment>
                <Grid container>
                    <Grid item xs={2}></Grid>
                    <Grid item xs={8}>
                        {parseInt( count / rotateBy) % imgsrc.length === i ?
                            <div style={{width:width, height:height, borderRadius:borderRadius}}>
                                <img src={imgsrc[i]} style={{width:'100%', height:'100%'}}/>
                            </div>
                        :''}
                    </Grid>

                    <Grid item xs={2}></Grid>
                </Grid>
            </React.Fragment>
        )
    }

    return(
        <React.Fragment>

            <Grid container>
                <Grid xs={3} style={{textAlign:'center'}}>
                    <IconButton onClick={handleBack}>
                        <ArrowBackIosIcon/>
                    </IconButton>
                </Grid>
                <Grid xs={6}>
                    {list}
                </Grid>
                <Grid xs={3} style={{textAlign:'center'}}>
                    <IconButton onClick={handleNext}>
                        <ArrowForwardIosIcon/>
                    </IconButton>
                </Grid>
            </Grid>

        </React.Fragment>
    )
}

export default Carousel;
